function ExportCsv() {

  const exportCsvFile =()=>{

    console.log("Hello");
    // var id = location.href.split("/details/")[1];
    var url = `${ngrokUrl}/api/v1/export_csv?company_id=Piou`;
    var anchor = document.createElement("a");
    anchor.setAttribute("href", url);
  anchor.setAttribute("download", "file.csv");
  anchor.click();


  }

  return (
      <Button variant="contained" onClick={exportCsvFile} style={{width:"240px"}}> Export </Button>
  );
}

export default ExportCsv;
