import { useState} from 'react';
import * as React from 'react';
import Button from '@material-ui/core/Button';
// import Grid from '@mui/material/Grid';
import Box from '@material-ui/core/Box';
import Input from '@mui/material/Input';
import 'react-toastify/dist/ReactToastify.css';
import { styled } from '@mui/material/styles';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell, { tableCellClasses } from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import {Container, Grid} from "@material-ui/core";
import { useHistory } from "react-router-dom";

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  '&:last-child td, &:last-child th': {
    border: 0,
  },
}));


const ariaLabel = { 'aria-label': 'description' };

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const rows = [
  createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
  createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
  createData('Eclair', 262, 16.0, 24, 6.0),
  createData('Cupcake', 305, 3.7, 67, 4.3),
  createData('Gingerbread', 356, 16.0, 49, 3.9),
];

const AddData = (props) => {


  const history = useHistory();
  const [userData, setUserData] = useState([]);
  const [url, setUrl] = useState('');
  const [name, setName] = useState('');
  const [type, setType] = useState('');
  const [foundationYear, setFoundationYear] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isFetched, setIsFetched] = useState(false);
  let table_details;
  const [companyDetail, setCompanyDetail] = useState({});
  const fetchCompanyDetail =(id) =>{
    console.log(id)
    history.push(`/details/${id}`)
    // const axios = require('axios').default;

    // // if (!isFetched){
    //   try {
    //     axios.get("http://localhost:4000/api/v1/companies/121", {
    //       headers:{
    //         'X-USER-TOKEN': localStorage.getItem('token'),
    //         "X-USER-EMAIL":localStorage.getItem('email')
    //       }
    //     })
    //     .then(function (response) {
    //       setIsLoading(false);
    //       if (response.data.success === false)
    //       {
    //         // setError(true);
    //         // setMessage(response.data.message);
    //         toast.error(response.data.message,  {
    //           position: "top-right",
    //         autoClose: 5000,
    //         hideProgressBar: false,
    //         closeOnClick: true,
    //         pauseOnHover: true,
    //         draggable: true,
    //         progress: undefined,
    //         });

    //         // alert(response.data.message)
    //       }
    //       else{

    //       setCompanyDetail(response.data);
    //       setIsFetched(true);
    //       // setIsFiltered(false);
    //       // setShowDetails(true);}
    //       // setError(false);
    //     }})
    //     .catch(function (error) {
    //       setIsLoading(false);
    //       console.log(error);
    //       toast.error("No Such Company Details Present",  {
    //         position: "top-right",
    //       autoClose: 5000,
    //       hideProgressBar: false,
    //       closeOnClick: true,
    //       pauseOnHover: true,
    //       draggable: true,
    //       progress: undefined,
    //       });
    //       // alert(error);
    //     })
    //   } catch (error) {
    //     setIsLoading(false);
    //     // alert(error)
    //     console.error(error);
    //     toast.error("No Such Company Details Present",  {
    //       position: "top-right",
    //     autoClose: 5000,
    //     hideProgressBar: false,
    //     closeOnClick: true,
    //     pauseOnHover: true,
    //     draggable: true,
    //     progress: undefined,
    //     });
    //   }


  }


  const switchDetailModeHandler = async (event) => {
    // let sent_url;
    event.preventDefault();
    const axios = require('axios').default;

    try {
      const headers = {
        'X-USER-TOKEN': localStorage.getItem('token'),
        "X-USER-EMAIL":localStorage.getItem('email')
      }

      var data = {
        name: name,
        url:url,
        company_type:type,
        foundation_year:foundationYear
      }

      axios.post("http://localhost:4000/api/v1/companies",data, {
          headers: headers
        }).then(function (response) {
        setIsLoading(false);
        if (response.data.success === false)
        {
          toast.error(response.data.message,  {
            position: "top-right",
          autoClose: 5000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          });

          // alert(response.data.message)
        }
        else{

        // setUserData(response.data)
        userData.push(response.data.company)
        setIsFetched(true);
        // setIsFiltered(false);
        // setShowDetails(true);
      }
        // setError(false);
      })
      .catch(function (error) {
        setIsLoading(false);
        console.log(error);
        toast.error("No Such Company Details Present",  {
          position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        });
        // alert(error);
      })
    } catch (error) {
      setIsLoading(false);
      // alert(error)
      console.error(error);
      toast.error("No Such Company Details Present",  {
        position: "top-right",
      autoClose: 5000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
      });
    }

  };


  if (isFetched)
  {
    table_details =(
      <Container maxWidth="lg">
      <Grid container>
      <Grid item xs={12}>
        <TableContainer component={Paper} >
            <Table sx={{ width: 776, marginTop:10, }} aria-label="customized table">
              <TableHead>
                <TableRow>
                  <StyledTableCell>Name</StyledTableCell>
                  <StyledTableCell align="right">Type</StyledTableCell>
                  <StyledTableCell align="right">Url</StyledTableCell>
                  <StyledTableCell align="right">Foundation Year</StyledTableCell>
                  <StyledTableCell align="right">Details</StyledTableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {userData.map((company) => (
                  <StyledTableRow key={company.name}>
                    <StyledTableCell component="th" scope="row">
                      {company.name}
                    </StyledTableCell>
                    <StyledTableCell align="right">{company.company_type}</StyledTableCell>
                    <StyledTableCell align="right">{company.url}</StyledTableCell>
                    <StyledTableCell align="right">{company.foundation_year}</StyledTableCell>
                    <StyledTableCell align="right">
                      <Button variant='outlined'
                        type='submit' sx = {{mt:20}}
                        name= {company.id} onClick={ () => fetchCompanyDetail(company.id)}
                      >
                        {/* {showDetails ? 'Hide Details' : 'Fetch Detais'} */}
                        Fetch Details
                      </Button>
                    </StyledTableCell>
                  </StyledTableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
      </Grid>
      </Grid>
      </Container>)
  };


