import { useState} from 'react';
import * as React from 'react';
import Button from '@material-ui/core/Button';
import Grid from '@mui/material/Grid';
import Box from '@material-ui/core/Box';
import Input from '@mui/material/Input';
import 'react-toastify/dist/ReactToastify.css';
import { styled } from '@mui/material/styles';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell, { tableCellClasses } from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Card from '../UI/Card';


const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  '&:last-child td, &:last-child th': {
    border: 0,
  },
}));

function createData(name, type, url, foundation_year, button) {
  return { name, type, url, foundation_year, button };
}

const rows = [
  createData('Protonshub Technologies', 'Type', 'Url', 2019, 'Fetch Details'),
];



const ariaLabel = { 'aria-label': 'description' };











const AddData = (props) => {

  const [userData, setUserData] = useState({});
  const [url, setUrl] = useState('');
  const [name, setName] = useState('');
  const [type, setType] = useState('');
  const [foundationYear, setFoundationYear] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isFetched, setIsFetched] = useState(false);
  let table_details;
  const switchDetailModeHandler = async (event) => {
    // let sent_url;
    event.preventDefault();
    const axios = require('axios').default;

    try {
      const headers = {
        'X-USER-TOKEN': localStorage.getItem('token'),
        "X-USER-EMAIL":localStorage.getItem('email')
      }

      var data = {
        company_name: name,
        url:url,
        company_type:type,
        foundation_year:foundationYear
      }

      axios.post("http://localhost:4000/api/v1/companies",data, {
          headers: headers
        }).then(function (response) {
        setIsLoading(false);
        if (response.data.success === false)
        {
          toast.error(response.data.message,  {
            position: "top-right",
          autoClose: 5000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          });

          // alert(response.data.message)
        }
        else{

        setUserData(response.data)
        setIsFetched(true);
        // setIsFiltered(false);
        // setShowDetails(true);
      }
        // setError(false);
      })
      .catch(function (error) {
        setIsLoading(false);
        console.log(error);
        toast.error("No Such Company Details Present",  {
          position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        });
        // alert(error);
      })
    } catch (error) {
      setIsLoading(false);
      // alert(error)
      console.error(error);
      toast.error("No Such Company Details Present",  {
        position: "top-right",
      autoClose: 5000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
      });
    }

  };


  if (isFetched)
  {
    table_details =(
      <TableContainer component={Paper} >
      <Table sx={{ width: 776, marginTop:10, }} aria-label="customized table">
        <TableHead>
          <TableRow>
            <StyledTableCell>Name</StyledTableCell>
            <StyledTableCell align="right">Type</StyledTableCell>
            <StyledTableCell align="right">Url</StyledTableCell>
            <StyledTableCell align="right">Foundation Year</StyledTableCell>
            <StyledTableCell align="right">Details</StyledTableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {userData.map((row) => (
            <StyledTableRow key={row.company.name}>
              <StyledTableCell component="th" scope="row">
                {row.name}
              </StyledTableCell>
              <StyledTableCell align="right">{row.company.type}</StyledTableCell>
              <StyledTableCell align="right">{row.company.url}</StyledTableCell>
              <StyledTableCell align="right">{row.company.foundation_year}</StyledTableCell>
              <StyledTableCell align="right">{row.company.button}</StyledTableCell>
            </StyledTableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>)



  }




  return (
    <section>

      <Grid container spacing={0} direction="column" alignItems="center" justifyContent="center">

          <form alignItems="center" onSubmit={switchDetailModeHandler} >
            <div >
              <Input placeholder="Name" inputProps={ariaLabel} onChange={e => setName(e.target.value)} required style={{width:"500px"}}  />
            </div>
            <div >
              <Input placeholder="Url" inputProps={ariaLabel} required onChange={e => setUrl(e.target.value)}   style={{width:"500px", marginTop:"10px"}}  />
            </div>
            <div >
              <Input placeholder="Type" inputProps={ariaLabel} required onChange={e => setType(e.target.value)}  style={{width:"500px", marginTop:"10px"}}  />
            </div>
            <div >
              <Input placeholder="Foundation Year" inputProps={ariaLabel} onChange={e => setFoundationYear(e.target.value)} style={{width:"500px", marginTop:"10px"}}  />
            </div>

            <Grid container spacing={2}>
              <Grid item>
                <Box pt={2}>
                  <Button variant='outlined'
                      type='submit' sx = {{mt:20}}
                      name="Fetch"
                    >
                      {/* {showDetails ? 'Hide Details' : 'Fetch Detais'} */}
                      Add
                    </Button>
                </Box>
              </Grid>

            </Grid>
          </form>
        </Grid>

        <TableContainer component={Paper} >
          <Table sx={{ width: 776, marginTop:10, }} aria-label="customized table">
            <TableHead>
              <TableRow>
                <StyledTableCell>Name</StyledTableCell>
                <StyledTableCell align="right">Type</StyledTableCell>
                <StyledTableCell align="right">Url</StyledTableCell>
                <StyledTableCell align="right">Foundation Year</StyledTableCell>
                <StyledTableCell align="right">Details</StyledTableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {rows.map((row) => (
                <StyledTableRow key={row.name}>
                  <StyledTableCell component="th" scope="row">
                    {row.name}
                  </StyledTableCell>
                  <StyledTableCell align="right">{row.type}</StyledTableCell>
                  <StyledTableCell align="right">{row.url}</StyledTableCell>
                  <StyledTableCell align="right">{row.foundation_year}</StyledTableCell>
                  <StyledTableCell align="right">{row.button}</StyledTableCell>
                </StyledTableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>


          {isFetched && <div> <Card>
          <ul><li>{table_details}</li></ul>
          </Card></div>}


        <ToastContainer position="top-right"
          autoClose={5000}
          hideProgressBar={false}
          newestOnTop={false}
          closeOnClick
          rtl={false}
          pauseOnFocusLoss
          draggable
          pauseOnHover/>

      </section>
