import Card from '../UI/Card';
import { useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';
import { styled, alpha } from '@mui/material/styles';
import ButtonBase from '@mui/material/ButtonBase';
import Box from '@material-ui/core/Box';
import Input from '@mui/material/Input';
// import Filters from './Filters'
// import { dataFiltered } from './Filters';
import Csv from './ExportCsv'

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import SearchIcon from '@mui/icons-material/Search';
import InputBase from '@mui/material/InputBase';




const AddData = (props) => {

  const [firstName, setUserFirstName] = useState();
  const [isFiltered, setIsFiltered] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const[filteredCompany, setFilteredCompany] = useState();
  // const[filterCall, setFilterCall] = useState(false);
  const firstNameChangeHandler = (event) => {
    setUserFirstName(event.target.value);
  };

  const [lastName, setUserLastName] = useState();

  const lastNameChangeHandler = (event) => {
    setUserLastName(event.target.value);
  };

  const [email, setUserEmail] = useState();

  const emailChangeHandler = (event) => {
    setUserEmail(event.target.value);
  };

  const [city, setUserCity] = useState();

  const cityChangeHandler = (event) => {
    setUserCity(event.target.value);
  };

  const [designation, setUserDesignation] = useState();

  const designationChangeHandler = (event) => {
    setUserDesignation(event.target.value);
  };

  const filterData =  async (event) => {
    event.preventDefault();
    const axios = require('axios').default;

    try {
      axios.get('http://localhost:4000/api/v1/search', {
        params: {
          first_name: firstName,
          last_name:lastName,
          city:city,
          email:email,
          designation:designation,
          company_name: company_name
        },
        headers:{
          'X-USER-TOKEN': localStorage.getItem('token'),
          "X-USER-EMAIL":localStorage.getItem('email')
        }
      })
      .then(function (response) {
        if (response.data.success === false)
        {
          // alert(response.data.message)
          toast.error(response.data.message,  {
            position: "top-right",
          autoClose: 5000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          });
          setIsFiltered(false)
        }
        else{

          // dataFiltered = response.data;
          setFilteredCompany(response.data)
          setIsFiltered(true);
          setIsFetched(false);
          // setFilterCall(true);
        }

      })
      .catch(function (error) {
        console.log(error);
        toast.error(error,  {
          position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        });
      })
    } catch (error) {
      console.error(error);
      toast.error(error,  {
        position: "top-right",
      autoClose: 5000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
      });
    }

  }

  const data_classes = useStyles();
  const [isFetched, setIsFetched] = useState(false);
  const [userData, setUserData] = useState({});
  const [showDetails, setShowDetails] = useState(false);
  const [url, setUrl] = useState('');
  const [name, setName] = useState('');

  const [error, setError] = useState(false);
  const[message, setMessage] = useState();

  let heading_title;
  let employee_details;
  let founders_details;

  let filtered_employee_details;
  let filtered_founders_details;

  const switchDetailModeHandler = async (event) => {
    let sent_url;
    event.preventDefault();
    if (event.nativeEvent.submitter.name === "Fetch"){
      sent_url = "http://localhost:4000/api/v1/company_profile"
    }
    else{
      setIsLoading(true);
      sent_url = "http://localhost:4000/api/v1/resync"
      // sent_url = "http://localhost:4000/api/v1/company_profile"

    }
    const axios = require('axios').default;

    // if (!isFetched){
      if(!showDetails){
      try {
        axios.get(sent_url, {
          params: {
            company_name: name,
            url:url
          },
          headers:{
            'X-USER-TOKEN': localStorage.getItem('token'),
            "X-USER-EMAIL":localStorage.getItem('email')
          }
        })
        .then(function (response) {
          setIsLoading(false);
          if (response.data.success === false)
          {
            setError(true);
            setMessage(response.data.message);
            toast.error(response.data.message,  {
              position: "top-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            });

            // alert(response.data.message)
          }
          else{

          setUserData(response.data)
          setIsFetched(true);
          setIsFiltered(false);
          setShowDetails(true);}
          setError(false);
        })
        .catch(function (error) {
          setIsLoading(false);
          console.log(error);
          toast.error("No Such Company Details Present",  {
            position: "top-right",
          autoClose: 5000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          });
          // alert(error);
        })
      } catch (error) {
        setIsLoading(false);
        // alert(error)
        console.error(error);
        toast.error("No Such Company Details Present",  {
          position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        });
      }

    }
    else
    {
      if (showDetails){
      setShowDetails(false);
      }
      else{
        setShowDetails(true);
      }
    }

  };

  if (isFetched)
  {
  }

  if (isFiltered)
  {

  }


  function Data(props){
    return (<div>


    </div>)
  }

  return (

    <section className={data_classes.dataClass}>

      <Grid container spacing={0} direction="column" alignItems="center" justifyContent="center">

        <form alignItems="center" onSubmit={switchDetailModeHandler}>
          <div >
            <Input placeholder="Name" inputProps={ariaLabel} required onChange={e => setName(e.target.value)} disabled={showDetails} style={{width:"500px"}}  />
          </div>
          <div >
            <Input placeholder="Url" inputProps={ariaLabel} required onChange={e => setUrl(e.target.value)} disabled={showDetails} style={{width:"500px", marginTop:"10px"}}  />
          </div>
          <div >
            <Input placeholder="Type" inputProps={ariaLabel} required onChange={e => setUrl(e.target.value)} disabled={showDetails} style={{width:"500px", marginTop:"10px"}}  />
          </div>
          <div >
            <Input placeholder="Foundation Year" inputProps={ariaLabel}  onChange={e => setUrl(e.target.value)} disabled={showDetails} style={{width:"500px", marginTop:"10px"}}  />
          </div>

          <Grid container spacing={2}>
            <Grid item>
              <Box pt={2}>
                <Button variant='outlined'
                    type='submit' sx = {{mt:20}}
                    className={data_classes.toggle} name="Fetch"
                  disabled = {isLoading}>
                    {/* {showDetails ? 'Hide Details' : 'Fetch Detais'} */}
                    Add Data
                  </Button>
              </Box>
            </Grid>

          </Grid>
        </form>
      </Grid>

      {error &&
        <span style={{ color: 'red' }}>{message}</span>}
          <ToastContainer position="top-right"
          autoClose={5000}
          hideProgressBar={false}
          newestOnTop={false}
          closeOnClick
          rtl={false}
          pauseOnFocusLoss
          draggable
          pauseOnHover/>
    </section>
  );
};

export default AddData;
