import { useState, useRef, useContext } from 'react';
import { Grid,Paper, TextField, Button, Typography,Link } from '@material-ui/core'
import { useHistory } from 'react-router-dom';
import AuthContext from '../../store/auth-context';
// import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
// import FormControlLabel from '@material-ui/core/FormControlLabel';
// import Checkbox from '@material-ui/core/Checkbox';
const AuthForm=({handleChange})=>{



  const history = useHistory();
  const emailInputRef = useRef();
  const passwordInputRef = useRef();

  const authCtx = useContext(AuthContext);

  const [isLogin, setIsLogin] = useState(true);
  const [isLoading, setIsLoading] = useState(false);

  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const enteredEmail = emailInputRef.current.value;
    const enteredPassword = passwordInputRef.current.value;

    if (enteredPassword.length < 5) {
      alert('Password must contain atleast 5 characters');
      return;
    }

    setIsLoading(true);
    let url;
    if (isLogin) {
      // url =
        // 'https://react-fetch-detail.herokuapp.com/users/sign_in';
        // url="http://localhost:4000/api/v1/auth"
      url = 'http://localhost:4000/users/sign_in'
    } else {
      // url =
        // 'https://react-fetch-detail.herokuapp.com/users';
        // url="http://localhost:4000/api/v1/auth/sign_in"
      url = 'http://localhost:4000/users'
    }
    // if (url === "https://react-fetch-detail.herokuapp.com/users")
    if (url === "http://localhost:4000/users")
    // if (url === "http://localhost:4000/api/v1/auth/sign_in")

    {
      fetch(url, {
        method: 'POST',
        body: JSON.stringify({
          user:
          {
            email: enteredEmail,
            password: enteredPassword,
          }
        }),
        headers: {
          'Content-Type': 'application/json',
        },
      })
      .then((res) => {
        setIsLoading(false);
        if (res.ok) {
          return res.json();
        } else {
          return res.json().then((data) => {
            let errorMessage = data.message;
            throw new Error(errorMessage);
          });
        }
      })
      .catch((err) => {
        alert(err.message);
        setIsLogin(true);
        history.replace('/auth');
      });
    }

    else {
    fetch(url, {
      method: 'POST',
      body: JSON.stringify({
        user:
        {
          email: enteredEmail,
          password: enteredPassword,
          returnSecureToken: true
        }
      }),
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((res) => {
        setIsLoading(false);
        if (res.ok) {
          return res.json();
        } else {
          return res.json().then((data) => {
            let errorMessage = data.message;
            throw new Error(errorMessage);
          });
        }
      })
      .then((data) => {
        const expirationTime = new Date(
          new Date().getTime() + 365*1000
        );
        authCtx.login(data.user.auth_token, expirationTime.toISOString());
        history.replace('/');
      })
      .catch((err) => {
        alert(err.message);
      });
    }
  };


    const paperStyle={padding :20,height:'48vh',width:300, margin:"96px auto", borderTop:'3px solid #4bacfe'}
    // const avatarStyle={backgroundColor:'#1bbd7e'}
    const btnstyle={margin:'8px 0'}
    return(
        <Grid>
            <Paper  style={paperStyle}>
                <Grid align='center'>
                    <h2>Sign In</h2>
                </Grid>

                <form onSubmit={submitHandler}>
              <div>

              <label htmlFor='email'>Your Email</label>
          <input type='email' id='email' required ref={emailInputRef} />
        </div>
        <div>
          <label htmlFor='password'>Your Password</label>
          <input
            type='password'
            id='password'
            required
            ref={passwordInputRef}
          />
        </div>

              {/* <TextField label='email' id="email" type="email" placeholder='Enter email' fullWidth required ref={emailInputRef}/>
                <TextField label='Password' placeholder='Enter password' id="password" type='password' fullWidth required ref={passwordInputRef}/> */}

          {/* <label htmlFor='email'>Your Email</label>
          <input type='email' id='email' required ref={emailInputRef} />
        </div>
        <div>
          <label htmlFor='password'>Your Password</label>
          <input
            type='password'
            id='password'
            required
            ref={passwordInputRef}
          />
        </div>
        <div className={classes.actions}>
          {!isLoading && (
            <button>{isLogin ? 'Login' : 'Create Account'}</button>
          )}
          {isLoading && <p>Sending request...</p>}
          <button
            type='button'
            className={classes.toggle}
            onClick={switchAuthModeHandler}
          >
            {isLogin ? 'Create new account' : 'Login with existing account'}
          </button>
          {/* <Button variant="contained">Hello World</Button> */}
        {/* </div> */}
      </form>






                <TextField label='Username' placeholder='Enter username' fullWidth required/>
                <TextField label='Password' placeholder='Enter password' type='password' fullWidth required/>
                <Button type='submit' color='primary' variant="contained" style={btnstyle} fullWidth>Sign in</Button>
                <Typography >
                     <Link href="#" >
                        Forgot password ?
                </Link>
                </Typography>
            </Paper>
        </Grid>
    )
}

export default AuthForm
