import * as React from 'react';
import {useParams} from 'react-router-dom';
import { useEffect} from 'react';
import Grid from '@mui/material/Grid';
import Card from '../UI/Card';
import { styled} from '@mui/material/styles';
import Paper from '@mui/material/Paper';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useState} from 'react';
import ButtonBase from '@mui/material/ButtonBase';
import DropDownFilter from "./DropDownFilter";
import { ngrokUrl } from '../../store/HostUrl';
import Pagination from '@material-ui/lab/Pagination';
import { useLocation } from 'react-router-dom';

const Img = styled('img')({
  margin: 'auto',
  display: 'block',
  maxWidth: '100%',
  maxHeight: '100%',
  height:'237px'
});

const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  padding: theme.spacing(1),
  color: theme.palette.text.secondary,
}));

const EmployeeData = (props) => {

  const { id } = useParams();
  console.log(id)
  let heading_title;
  let employee_details;
  let founders_details;

  const [designation2, setDesignation2] = useState([''])
  const [state, setState] = useState('')
  const [country, setCountry] = useState('')
  const [city, setCity] = useState('')

  const [isFetched, setIsFetched] = useState(false);
  const [userData, setUserData] = useState({});
  const [showDetails, setShowDetails] = useState(false);
  const [ userDataBackup,setUserDataBackup] = useState({})
  const [totalPages, setTotalPages] = useState();
  const [currentPage, setCurrentPage] = useState(1);
  const [isFiltered, setIsFiltered] = useState(false);

  useEffect(() => {
      const axios = require('axios').default;
      try {
        axios.get(`${ngrokUrl}/api/v1/companies/${id}`, {
          headers:{
            'X-USER-TOKEN': localStorage.getItem('token'),
            "X-USER-EMAIL":localStorage.getItem('email')
          },
        })
        .then(function (response) {
          if (response.data.success === false)
          {
            // alert(response.data.message)
            toast.error(response.data.message,  {
              position: "top-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            });
            // setIsFiltered(false)
          }
          else{
            setUserData(response.data);
            setUserDataBackup(response.data);
            setIsFetched(true);
            setTotalPages(response.data.pagination.total_pages);
            // setIsFiltered(false);
            setShowDetails(true);
            setCurrentPage(response.data.pagination.current_page);
          }
            // setError(false);
        })
        .catch(function (error) {
          console.log(error);
          toast.error(error,  {
            position: "top-right",
          autoClose: 5000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          });
        })
      } catch (error) {
        console.error(error);
        toast.error(error,  {
          position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        });
      }
  }, [id]);

  if (isFetched)
  {
    console.log(userData)
    heading_title = (
    <div>
      <h2>{userData.company.name}</h2>
      <p> {userData.company.tagline}</p>
      <p>Followers:{userData.company.followers}</p>
      <p>No of employees:{userData.company.no_of_employees}</p>
       {/* <h2>Protonshub</h2>
      <p> Tagline:</p>
      <p>Followers:</p>
      <p>No of employees:</p> */}

    </div>
    );
    // company_name = userData.company_detail.name;

    console.log(userData,"deta")

    employee_details = userData.employee_details.map((user) => (
      <Data user = {user}/>
    ));

    founders_details = userData.founders_details.map((user) => (
      <Data user = {user}/>
      ));

  }

  function Data(props){
    return (<div>
      <Card>
